INSERT INTO tb_game (title, game_year, genre, platforms, score, img_url, short_description, long_description) VALUES ('Diablo IV', 2023, 'Ação', 'PC', 57.2, 'https://blz-contentstack-images.akamaized.net/v3/assets/blt77f4425de611b362/blt6d7b0fd8453e72b9/646e720a71d9db111a265e8c/d4-open-graph_001.jpg', 'Diablo IV é um RPG de ação desenvolvido pela empresa Blizzard Entertainment, sendo o quarto título da série Diablo', 'Situado no mundo de Santuário, Diablo IV se passa logo após Diablo III: Reaper of Souls. Cultistas invocaram a principal antagonista e filha de Mephisto, Lilith. Depois dos eventos dos jogos anteriores.');
INSERT INTO tb_game (title, game_year, genre, platforms, score, img_url, short_description, long_description) VALUES ('Dead Space Remake', 2023, 'Horror', 'PC, PS5, Xbox', 41.6, 'https://cdn1.epicgames.com/offer/4ec958d5e4b6429aadbc116cee0b6ea0/EGS_DeadSpace_MotiveStudio_S1_2560x1440-48626121e40a971f59b60451fa3d8636', 'Dead Space Remake é um jogo eletrônico de survival horror e ficção científica desenvolvido pela Motive Studios e publicado pela Electronic Arts.', 'O jogo mantém o mesmo enredo básico do original. Situado no século 26, a história segue o engenheiro Isaac Clarke, um tripulante em uma embarcação de reparos designada para o USG Ishimura, uma enorme nave de mineração planetária.');
INSERT INTO tb_game (title, game_year, genre, platforms, score, img_url, short_description, long_description) VALUES ('Resident Evil 4 Remake', 2023, 'Horror', 'PC, PS4, PS5, Xbox', 67.8, 'https://jornalogoncalense.com.br/wp-content/uploads/2022/06/idmjlql391g51.png', 'Resident Evil 4, conhecido no Japão como Biohazard RE:4, é um jogo eletrônico de survival horror desenvolvido e publicado pela Capcom.', 'Assim como no jogo original, o jogador organiza seu inventário com uma "caixa de anexos". Novidade no remake, um sistema de criação permite ao jogador criar itens e munições usando recursos coletados ao longo do jogo.');
INSERT INTO tb_game (title, game_year, genre, platforms, score, img_url, short_description, long_description) VALUES ('Star Wars Jedi: Survivor', 2023, 'Aventura', 'PC, PS5, Xbox', 56.5, 'https://gamerspoilerbrasil.b-cdn.net/wp-content/uploads/2023/06/Jedi-Switch-1.jpeg', 'Star Wars Jedi: Survivor é um jogo de ação e aventura desenvolvido pela Respawn Entertainment e publicado pela Electronic Arts.', 'O rastreamento de raio em tempo real, entre outros recursos de última geração, de acordo com Stig Asmussen, permite que o Respawn funcione "com uma qualidade muito acima de qualquer coisa que já desenvolvemos antes".');
INSERT INTO tb_game (title, game_year, genre, platforms, score, img_url, short_description, long_description) VALUES ('The Legend of Zelda: Tears of the Kingdom', 2023, 'Aventura', 'Nitendo', 80.2, 'https://wp-uploads.qualbert.com/2023/06/Tears-of-the-Kingdom-wallpaper-1170x720.jpg', 'The Legend of Zelda: Tears of the Kingdom é um jogo eletrônico de ação-aventura desenvolvido e publicado pela Nintendo.', 'Tears of the Kingdom recebeu aclamação universal no agregador de notas Metacritic. A crítica especializada elogiou o mundo aberto expandido introduzido em Breath of the Wild.');